function aesplainencryptfun () {

    
    let plainvalue = document.getElementById("aesplainttext").value
    let key = document.getElementById("aesplaintkey").value

    let encrpyted = CryptoJS.AES.encrypt(plainvalue,key)
    document.getElementById("aesplainvalue").innerHTML = "<b>" + "Output : " +"</b>" + encrpyted.toString()

}




function aesplaindecryptfun () {


    let encvalue = document.getElementById("aesplainencstring").value
    let key = document.getElementById("aesplainenckey").value

    let decrpyted = CryptoJS.AES.decrypt(encvalue,key)
    document.getElementById("aesplaindecvalue").innerHTML = "<b>" + "Output : " +"</b>" + decrpyted.toString(CryptoJS.enc.Utf8)


}

function aesencPkcs7vfun () {

    let plainvalue = document.getElementById("aesencPkcs7text").value
    let key1 = document.getElementById("aesencPkcs7key").value
    let iv1 = document.getElementById("aesencPkcs7iv").value

    var key = CryptoJS.enc.Utf8.parse(key1);
    var iv = CryptoJS.enc.Utf8.parse(iv1)

    let encrypted = CryptoJS.AES.encrypt(CryptoJS.enc.Utf8.parse(plainvalue), key,
            {
                keySize: 128 / 8,
                iv: iv,
                mode: CryptoJS.mode.CBC,
                padding: CryptoJS.pad.Pkcs7
            });


    document.getElementById("aesencPkcs7value").innerHTML = "<b>" + "Output : " +"</b>" + encrypted
}


function aesdecPkcs7fun () {

    let encvalue = document.getElementById("aesdecPkcs7string").value
    
    let key = CryptoJS.enc.Utf8.parse(document.getElementById("aesdecPkcs7key").value);
    let iv = CryptoJS.enc.Utf8.parse(document.getElementById("aesdecPkcs7iv").value);
    


    var decrypted = CryptoJS.AES.decrypt(encvalue, key,
        {
            keySize: 128 / 8,
            iv: iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
        })
    


    document.getElementById("aesdecPkcs7value").innerHTML = "<b>" + "Output : " +"</b>" + decrypted.toString(CryptoJS.enc.Utf8)
    //let output = decrpyted.toString(CryptoJS.enc.Utf8)

    

}